}
impl<T, const len: usize> Wrap<T, len> { /// Constructor. pub fn new(arr: [T; len], grouping: usize) -> Self { if len % grouping != 0 { panic!( "grouping value {} is not a multiple of array length ({})", grouping, len, ); } if grouping < 1 { panic!("illegal grouping value {}, must be > 0", grouping) } Self { arr, grouping } } /// Grouped fold. pub fn fold<Acc>(&self, init: Acc, mut next: impl FnMut(Acc, &[T]) -> Acc) -> Acc { let (arr, grouping) = (&self.arr, self.grouping); let mut acc = init; let mut i = 0; while i < len { let next_i = i + grouping; acc = next(acc, &arr[i..next_i]); i = next_i } acc }
